	 f.TotalPaid, idFacture, CONVERT(CHAR, f.FeesDate, 105) 'FeesDate', Note, SchoolYear, idMonth, s.photo
 from tblStudent s JOIN tblDiscount d on s.NumInscription = d.NumInscription
 JOIN DetailPaiment dp on dp.NumInscription = s.NumInscription
 JOIN tblFees F on f.idFees = dp.idFacture
 JOIN tblFeetype ft on ft.idFeesType = dp.idMonth
 JOIN tblschoolYear sy on sy.idyear = s.idyear
 JOIN Classes C on C.idClass = s.classID
 JOIN tblFiliere fi on fi.idFiliere = c.idFiliere
 Where S.NumInscription = @NumInscription and sy.SchoolYear = @schoolYear
 ORDER by ft.idFeesType

